site.headStyle:
html, body { scroll-behavior: smooth; }

@media (min-width:480px) {
  body .container-element.section-element.hero {
    height: 100vh;
  }
}

@media (min-width:480px) {
  .silex-runtime .image .image-hover {
    opacity: 0;
    transition: opacity .3s ease;
  }

  .silex-runtime .image .image-hover:hover {
    opacity: 1;
  }
}
